use bitcoin::{consensus, Block};
use bitcoin_demo::{bitcoin_new_to_old, bitcoin_rpc_testnet4};
use bitcoincore_rpc::RpcApi;
use hex_literal::hex;

fn main() {
    let rpc = bitcoin_rpc_testnet4().unwrap();
    let block: Block = consensus::deserialize(&hex!("")).unwrap();
    println!("{:?}", block);
    loop {
        let result = rpc.submit_block(&bitcoin_new_to_old(&block));
        if result.is_ok() {
            println!("Done. Submit result: {:?}", result);
            break;
        }
    }
}
